-- create schema
CREATE SCHEMA if NOT EXISTS spring_example_jdbc;

-- set schema as deafult
USE spring_example_jdbc;

-- drop any exisiting table

DROP TABLE IF EXISTS authorities;
DROP TABLE IF EXISTS orders;
DROP TABLE IF EXISTS product_category_many_to_many;
DROP TABLE IF EXISTS products;
DROP TABLE IF EXISTS categories;
DROP TABLE IF EXISTS user_activities;
DROP TABLE IF EXISTS users;


-- create new tables

CREATE TABLE users (
  -- default for Spring security
  username varchar(50) NOT NULL PRIMARY KEY,
  password varchar(64) NOT NULL,
  enabled BOOLEAN NOT NULL,

  -- custom added
  added DATETIME,
  first_name VARCHAR(64),
  last_name VARCHAR(64),
  full_address VARCHAR(512),
  phone VARCHAR(16)
);

create table authorities (
  -- default for Spring security
  username VARCHAR(50) NOT NULL,
  authority VARCHAR(50) NOT NULL,
  CONSTRAINT fk_authorities_users FOREIGN KEY(username) REFERENCES users(username) ON DELETE CASCADE
);
CREATE UNIQUE INDEX ix_auth_username ON authorities (username,authority);



CREATE TABLE products (
  id INT PRIMARY KEY AUTO_INCREMENT,
  price FLOAT(6,2) NOT NULL,
  stock INT,
  name VARCHAR(64) NOT NULL,
  description VARCHAR(512) NOT NULL,
  image_base_64 MEDIUMTEXT,
  added DATETIME
);

CREATE TABLE categories (
  id INT PRIMARY KEY AUTO_INCREMENT,
  name VARCHAR(64) NOT NULL
);

CREATE TABLE product_category_many_to_many (
  fk_product_id INT, 
  fk_category_id INT,
  PRIMARY KEY (fk_product_id, fk_category_id),

  CONSTRAINT fk_product_join_category FOREIGN KEY(fk_product_id) REFERENCES products(id) ON DELETE CASCADE,
  CONSTRAINT fk_category_join_product FOREIGN KEY(fk_category_id) REFERENCES categories(id) ON DELETE CASCADE
);

CREATE TABLE user_activities (
  id INT PRIMARY KEY AUTO_INCREMENT,
  tag varchar(64),
  before_value MEDIUMTEXT,
  after_value MEDIUMTEXT,
  added DATETIME NOT NULL,
  fk_username VARCHAR(50) NOT NULL,

  CONSTRAINT fk_user_activity FOREIGN KEY(fk_username) REFERENCES users(username) ON DELETE CASCADE
);

CREATE TABLE orders (
  id INT PRIMARY KEY AUTO_INCREMENT,
  added DATETIME NOT NULL,
  amount FLOAT(10,2) NOT NULL,
  username VARCHAR(50),
  full_name varchar(64),
  full_address varchar(512),
  products_json TEXT
);


